import React, { Component } from 'react'
import { XTerm, Terminal } from 'react-xterm'

class XtermTerminal extends Component {
  componentDidMount() {
    runFakeTerminal(this.refs.xterm)
  }
  componentWillUnmount() {
    this.refs.mainDeviceComponent.componentWillUnmount()
  }

  sendToTerminal(text) {
    this.refs.xterm && this.refs.xterm.writeln('Welcome to xterm!!@@##.js')
  }

  render() {
    return (
      <div>
        <button onClick={() => {
          var iframe = `<html><head><style>body, html {width: 100%; height: 100%; margin: 0; padding: 0}</style></head><body><iframe src='http://localhost:3000/' style='height:calc(100% - 4px);width:calc(100% - 4px)'></iframe></html></body>`;

          var win = window.open('', '', 'width=600,height=480,toolbar=no,menubar=no,resizable=yes');
          win.document.write(iframe);
        }}>
          Open
        </button>

        <XTerm ref='xterm' style={{
          addons: ['fit', 'fullscreen', 'search'],
          overflow: 'hidden',
          position: 'relative',
          width: '100%',
          height: '100%'
        }} />


      </div>
    )
  }
}

export default XtermTerminal

function runFakeTerminal(xterm: XTerm) {
  const term: Terminal = xterm.getTerminal()
  var shellprompt = '$ '

  function prompt () {
    xterm.write('\r\n' + shellprompt)
  }
  xterm.writeln('Welcome to xterm.js')
  xterm.writeln('This is a local terminal emulation, without a real terminal in the back-end.')
  xterm.writeln('Type some keys and commands to play around.')
  xterm.writeln('')
  prompt()

  term.on('key', function (key, ev) {
    var printable = (
      !ev.altKey && !ev.ctrlKey && !ev.metaKey
    )

    if (ev.keyCode === 13) {
      prompt()
    // } else if (ev.keyCode == 8) {
    //   // Do not delete the prompt
    //   if (term['x'] > 2) {
    //     xterm.write('\b \b')
    //   }
    } else if (printable) {
      xterm.write(key)
    }
  })

  term.on('paste', function (data, ev) {
    xterm.write(data)
  })
}
